import {BehaviorSubject, Subject, Observable} from './libs/rxjs.js';
import assert                                 from 'power-assert';
import {humanMsg}                             from 'utilities';
import {constraint}                           from './util/misc.js';

type Iterator<T> = {
	next: () => ({
		done: boolean,
		value: T
	})
}
type Iterable<T> = {
	// [Symbol.iterator: Symbol]: () => Iterator<T> // Flow does not accept this
}

const $$set               = Symbol('$$set');
const $$addSubject        = Symbol('$$addSubject');
const $$deleteSubject     = Symbol('$$deleteSubject');
const $$valueObservable   = Symbol('$$valueObservable');
const $$disableNextReplay = Symbol('$$disableNextReplay');

class AddReplaySubject extends Subject {
	constructor(initialSet) {
		super();
		assert(initialSet[Symbol.iterator]);
		this._setReference = initialSet;
	}
	normalSubscribe(...args) {
		this[$$disableNextReplay] = true;
		return this.subscribe(...args);
	}
	// noinspection JSDuplicatedDeclaration
	_subscribe(subscriber) {
		const subscription = super._subscribe(subscriber);
		if (subscription && !subscription.isUnsubscribed && !this[$$disableNextReplay]) {
			this._setReference.forEach(::subscriber.next);
		}
		this[$$disableNextReplay] = false;
		return subscription;
	}
}

/**
 * A data-structure with the same interface as `Set`,
 * and emits mutation events from rxjs streams.
 */
export default class ObservableSet<T> {

	/**
	 * Create a new instance of `ObservableSet`.
	 * @param {Iterable<T>} [initialContent] - initial elements to put in this set object
	 */
	constructor(initialContent: Iterable<T> = []) {
		/* define private fields */
		/** @private */ this[$$set]             = new Set();
		/** @private */ this[$$addSubject]      = new AddReplaySubject(this);
		/** @private */ this[$$deleteSubject]   = new Subject();

		const valueSubject = new BehaviorSubject(new Set(this));

		/** @private */ this[$$valueObservable] = valueSubject.asObservable();

		/* link streams */
		this[$$addSubject]   .normalSubscribe(::this.add);
		this[$$deleteSubject].subscribe      (::this.delete);
		this[$$addSubject]   .normalSubscribe(() => { valueSubject.next(new Set(this)) });
		this[$$deleteSubject].subscribe      (() => { valueSubject.next(new Set(this)) });

		/* add initial content */
		initialContent.forEach(::this.add);
	}

	/**
	 * Get an event stream associated with this set object.
	 * @param {string} op - the name of the event stream you want: `"add"` or `"delete"`
	 * @returns {Subject} - the rxjs event stream
	 */
	e(op: string): Observable {
		switch (op) {
			case 'add':    return this[$$addSubject];
			case 'delete': return this[$$deleteSubject];
			default: constraint(false, humanMsg`
				The ${op} event does not exist.
			`);
		}
	}

	/**
	 * Get an event stream associated with this set object.
	 * @param {string} name  - the name of the property you want; the
	 *                         only one available at the moment is `"value"`
	 * @returns {Observable} - the rxjs event stream (based on the `BehaviorSubject` class)
	 */
	p(name: string): Observable {
		switch (name) {
			case 'value': return this[$$valueObservable];
			default: constraint(false, humanMsg`
				The ${name} property does not exist.
			`);
		}
	}

	/**
	 * Add a new element with the given value to this set object.
	 * @param   {T} val         - the value to add to the set
	 * @returns {ObservableSet} - this observable set object
	 */
	add(val: T): this {
		if (!this[$$set].has(val)) {
			this[$$set].add(val);
			this[$$addSubject].next(val);
		}
		return this;
	}

	/**
	 * Remove all elements from this set object.
	 * @returns {void}
	 */
	clear(): void {
		for (let value of this[$$set]) {
			this.delete(value);
		}
	}

	/**
	 * Removes the element with the value and returns the value that `has(val)` would have previously returned.
	 * @param   {T} val   - the value to remove
	 * @returns {boolean} - the element that was removed, if it existed; `false` otherwise
	 */
	delete(val: T): boolean {
		if (this[$$set].has(val)) {
			this[$$set].delete(val);
			this[$$deleteSubject].next(val);
			return true;
		}
		return false;
	}

	/**
	 * @returns {Iterator<T[]>} - a new Iterator object that yields an array of
	 *                              `[value, value]` for each element in this set object.
	 */
	entries(): Iterator<[T, T]> {
		return this[$$set].entries();
	}

	/**
	 * Run a function for each element in this set object.
	 * @param {function(:T, :T)} callbackFn - the function to invoke for each element
	 * @param {Object} [thisArg]            - the object that will be `this` inside the `callbackFn`
	 * @returns {void}
	 */
	forEach(callbackFn: (T, T)=>void, thisArg?: Object): void {
		return this[$$set].forEach(callbackFn, thisArg);
	}

	/**
	 * Test whether this set object contains a certain value.
	 * @param {T} val - the value to test for
	 * @returns {boolean} `true`, if `val` is an element in this set object; `false` otherwise
	 */
	has(val: T): boolean {
		return this[$$set].has(val);
	}

	/**
	 * @returns {Iterator<T>} - a new Iterator object that contains the values for each element in this set object
	 */
	values(): Iterator<T> {
		return this[$$set].values();
	}

	/**
	 * The presence of this method makes an observable set object iterable through `for...of` loops.
	 * @returns {Iterator<T>} - a new Iterator object that contains the values for each element in this set object
	 */
	[Symbol.iterator](): Iterator<T> {
		return this[$$set][Symbol.iterator]();
	}

	/**
	 * @returns {number} - the number of values in this set object
	 */
	get size(): number {
		return this[$$set].size;
	}

	/**
	 * Overwrite the content of this set object,
	 * emitting the proper `delete` and `add` signals (in that order).
	 * @param {Iterable<T>} newContent - the new elements of the set
	 * @returns {ObservableSet} - this observable set object
	 */
	overwrite(newContent: Iterable<T>) {
		newContent = new Set(newContent);
		for (let e of this.values()) {
			if (!newContent.has(e)) {
				this.delete(e);
			}
		}
		for (let e of newContent) {
			if (!this.has(e)) {
				this.add(e);
			}
		}
		return this;
	}
}

import defaults from 'lodash-bound/defaults';
import {
	defineProperty,
	defineProperties
} from 'bound-native-methods';
import {
	mapOptionalArray,
	wrapInArray,
	definePropertyByValue
} from 'utilities';

import Module from './Module';


/**
 * Typed Modules allow to more easily create related
 * Type, Template and HasType classes. For example,
 * to create LyphType and LyphTemplate resources and
 * their HasType relationship from one description.
 **/
export default class TypedModule extends Module {

	TYPED_RESOURCE(config) {
		return mapOptionalArray(config, (conf) => {

			this.basicNormalization(conf);

			const superClasses = wrapInArray(conf.extends    || [this.classes.vertexValue('Template')]);
			const subClasses   = wrapInArray(conf.extendedBy || []);

			/* handling properties */
			conf::defaults({
				properties:        {},
				patternProperties: {}
			});

			/* Template class */
			const newTemplateClass = this.RESOURCE({

				name: conf.name,

				abstract: !!conf.abstract,

				extends:  superClasses,
				extendedBy: subClasses,

				singular: conf.singular,
				plural: conf.plural,

				icon: conf.icon,

				properties:        conf.properties,
				patternProperties: conf.patternProperties,

				behavior: conf.behavior

			});

			// TODO: go back to a specific Type class for each Template Class
			// -- for now, all Template subclasses have a reference to the only Type class
			const Type = this.classes.vertexValue('Type');
			newTemplateClass::definePropertyByValue('Type', Type);

			/* register and return */
			return newTemplateClass;
		});

	}

}

import {pick, isFunction, isUndefined, values} from 'lodash-bound';

import {defineProperty} from 'bound-native-methods';

import assert from 'power-assert';

import {ValueTracker, property, flag, callOrReturn, humanMsg} from 'utilities';

import {
	$$owner,
	$$key,
	$$desc,
	$$value,
	$$initSet,
	$$entriesIn,
	$$destruct
} from './symbols';
const $$fieldsInitialized = Symbol('$$fieldsInitialized');
const $$aliases           = Symbol('$$aliases');

import {constraint} from "../util/misc";

/** @private */
export default (env) => {
	/**
	 * A class that represents specific fields on an `Entity`.
	 * @public
	 */
	class Field extends ValueTracker {

		////////////
		// static //
		////////////

		static augmentClass(cls, onlyForKey) {
			/* allow each kind of field to perform its initializations */
			for (let FieldClass of env.fieldClasses::values()) {
				if (!FieldClass[$$entriesIn]) { continue }
				for (let { key, desc, aliases } of FieldClass[$$entriesIn](cls)) {
					if (!onlyForKey || onlyForKey === key) {
						FieldClass.initClass({ cls, key, aliases, desc });
					}
				}
			}
		}

		static initializeEntity(owner, initialValues) {
			if (owner.fields) { return }
			owner::defineProperty('fields', { value: {} });

			/* initialize all fields */
			const keyDescs = {};
			for (let FieldClass of env.fieldClasses::values()) {
				if (!FieldClass[$$entriesIn]) { continue }
				for (let entry of FieldClass[$$entriesIn](owner.constructor)) {
					const { key, aliases = [] } = entry;
					const candidateKeys  = [key, ...aliases].filter(v => !initialValues[v]::isUndefined());
					constraint(candidateKeys.length <= 1, humanMsg`
						You cannot set the fields '${candidateKeys.join("', '")}'
						at the same time for a ${owner.constructor.singular}.
					`);
					keyDescs[key] = {
						...entry,
						owner,
						key,
						initialValue: initialValues[candidateKeys[0]],
						FieldClass
					};
				}
			}

			/* add related descriptions to each description */
			for (let entry of keyDescs::values()) {
				entry.related = keyDescs::pick(entry.relatedKeys);
			}

			/* create a field for each description */
			for (let entry of keyDescs::values()) {
				let { FieldClass } = entry;
				delete entry.FieldClass;
				new FieldClass(entry);
			}

			/* notify completion of field initialization */
			owner.pSubject('fieldsInitialized').next(true);
		}

		static destructEntity(owner) {
			for (let field of owner.fields::values()) {
				field[$$destruct]();
			}
		}

		static isEqual(a, b) { return a === b }


		/////////////////////////
		// events & properties //
		/////////////////////////

		@property()              value;
		@flag({ initial: true }) isPlaceholder;


		//////////////
		// instance //
		//////////////

		//noinspection JSDuplicatedDeclaration // (to suppress Webstorm bug)
		get [Symbol.toStringTag]() {
			return `Field: ${this[$$owner].constructor.name}#${this[$$key]}`;
		}

		constructor({ owner, key, desc, aliases = [], setValueThroughSignal = true, isPlaceholder = false }) {
			super();
			owner.fields[key] = this;
			for (let alias of aliases) {
				owner.fields[alias] = this;
			}
			this[$$owner]   = owner;
			this[$$key]     = key;
			this[$$desc]    = desc;
			this[$$aliases] = aliases;
			if (setValueThroughSignal) {
				this.p('value').subscribe(::this.set);
			}
			this.isPlaceholder = isPlaceholder;
		}

		static valueToJSON() { assert(false, humanMsg`Field.valueToJSON must be implemented in subclasses.`) }

		valueToJSON(options = {}) { return this.constructor.valueToJSON(this.value, options) }

		get() { return this[$$value] }

		set(newValue, options = {}) {
			if (!this.constructor.isEqual(this[$$value], newValue)) {
				const {
					      ignoreReadonly   = false,
					      ignoreValidation = false
				      }                    = options;
				constraint(ignoreReadonly || !this[$$desc].readonly, humanMsg`
					Tried to set the readonly field
					'${this[$$owner].constructor.name}#${this[$$key]}'.
				`);
				if (!ignoreValidation) { this.validate(newValue, ['set']) }
				this[$$value] = newValue;
				this.pSubject('value').next(newValue);
			}
		}

		validate(val, stages = []) {
			// to be implemented in subclasses
		}

		[$$destruct]() {
			// to be implemented in subclasses
		}

		[$$initSet](...alternatives) {
			for (let [guard, value] of alternatives) {
				if (guard::callOrReturn(this)) {
					if (value::isUndefined()) { return }
					value = value::callOrReturn();
					this.validate(value, ['initialize', 'set']);
					this.set(value, {
						ignoreReadonly:   true,
						ignoreValidation: true
					});
					return;
				}
			}
		}

	}
	return Field;
}

import {isUndefined, entries, cloneDeep} from 'lodash-bound';
import {defineProperty}                  from 'bound-native-methods';
import {humanMsg, callOrReturn}          from 'utilities';

import {constraint} from "../util/misc";

import Field_factory from './Field.js';

import {
	$$owner,
	$$key,
	$$desc,
	$$initSet,
	$$entriesIn
} from './symbols';

const $$ignoreReadonly = Symbol('$$ignoreReadonly');


/** @private */
export default (env) => {
	const Field = Field_factory(env);
	/**
	 * A field on an `Entity` representing a simple data-type.
	 * @public
	 */
	class PropertyField extends Field {

		// this[$$owner]   instanceof   RelatedTo | Resource
		// this[$$key]     instanceof   "name" | "class" | "id" | ...
		// this[$$value]   instanceof   any

		////////////
		// static //
		////////////

		static initClass({ cls, key, desc: {readonly} }) {
			if (cls.prototype.hasOwnProperty(key)) { return }
			cls.prototype::defineProperty(key, {
				get() { return this.fields[key].get() },
				...(readonly ? {} : {
					set(val) { this.fields[key].set(val)}
				}),
				enumerable:   true,
				configurable: false
			});
		}

		static [$$entriesIn](cls) {
			return cls.properties::entries()
				.map(([key, desc])=>({
					key,
					desc
				}));
		}


		//////////////
		// instance //
		//////////////

		constructor(options) {
			super(options);
			const { owner, desc, initialValue } = options;

			/* set the initial value */
			this.p('isPlaceholder').filter(v=>!v).take(1).subscribe(() => {
				this[$$initSet](
					[!initialValue::isUndefined(), () => initialValue::callOrReturn(owner)::cloneDeep()],
					['default' in desc,            () => desc.default::callOrReturn(owner)::cloneDeep()],
					['value'   in desc,            () => desc.value  ::callOrReturn(owner)::cloneDeep()],
					[!desc.required]
				);
			});
		}

		static valueToJSON(value, {flattenFieldValues = false} = {}) {
			if (flattenFieldValues) { value = JSON.stringify(value) }
			return value;
		}

		static jsonToValue(json, {flattenFieldValues = false} = {}) {
			if (flattenFieldValues) { json = JSON.parse(value) }
			return json;
		}

		validate(val, stages = []) {

			if (stages.includes('commit')) {
				constraint(!this[$$desc].required || !val::isUndefined(), humanMsg`
				    No value given for required field
				    '${this[$$owner].constructor.name}#${this[$$key]}'.
				`);
			}

			// TODO: CHECK CONSTRAINT: given property value conforms to JSON schema
			// TODO: CHECK ADDITIONAL (PROPERTY-SPECIFIC) CONSTRAINTS:
			//     : e.g., if this is a template, does it conform to its corresponding type?

		}

	}

	return env.registerFieldClass('PropertyField', PropertyField);
};

import {inRange, get, size, entries} from 'lodash-bound';

import {defineProperty} from 'bound-native-methods';

import assert from 'power-assert';

import ObservableSet from '../ObservableSet';
import {humanMsg, callOrReturn} from 'utilities';
import {constraint, setEquals} from '../util/misc';

import RelField_factory from './RelField.js';

import {
	$$owner,
	$$key,
	$$desc,
	$$value,
	$$entriesIn,
	$$destruct, $$initSet
} from './symbols';


/** @private */
export default (env) => env.registerFieldClass('Rel$Field', class Rel$Field extends RelField_factory(env) {

	// this[$$owner] instanceof Resource
	// this[$$key]   instanceof "-->ContainsMaterial" | "-->HasPart" | "<--FlowsTo" | ...
	// this[$$value] instanceof Set<IsRelatedTo>

	////////////
	// static //
	////////////

	static initClass({ cls, key, aliases, desc: {readonly} }) {

		// console.log('Rel$Field.initClass:', cls.name, key, aliases);

		assert(cls.isResource);
		if (cls.prototype.hasOwnProperty(key)) { return }
		for (let k of [key, ...aliases]) {
			cls.prototype::defineProperty(k, {
				get() { return this.fields[k].get() },
				...(readonly ? {} : {
					set(val) { this.fields[k].set(val)}
				}),
				enumerable:   true,
				configurable: false
			});
		}
	}

	static [$$entriesIn](cls) {
		if (!cls.isResource) { return [] }
		return cls.relationships::entries()
			.filter(([,rel]) => rel.cardinality.max > 1)
			.map(([key, desc]) => ({
				key,
				desc,
				aliases: desc.shortcutKey ? [desc.shortcutKey] : []
			}));
	}

	static isEqual: setEquals;


	//////////////
	// instance //
	//////////////

	constructor(options) {
		super({ ...options, setValueThroughSignal: false });
		const { owner, desc, initialValue, related } = options;

		/* initialize an empty observable set */
		this::defineProperty($$value, { value: new ObservableSet });

		/***/
		this.p('isPlaceholder').filter(v=>!v).take(1).subscribe(() => {

			/* set the initial value */
			const initialScValue = related::get([desc.shortcutKey, 'initialValue']);
			constraint(!initialValue || !initialScValue, humanMsg`
				You cannot set the fields '${desc.keyInResource}' and '${desc.shortcutKey}'
				at the same time for a ${this.constructor.singular}.
			`);
			this[$$initSet](
				[initialValue   && initialValue  [Symbol.iterator], () => initialValue  ::callOrReturn(owner)],
				[initialScValue && initialScValue[Symbol.iterator], () => initialScValue::callOrReturn(owner)],
				[desc.cardinality.min === 0]
			);

			/* synchronize with the other side */
			this.get().e('add').switchMap(
				other => other.p('fieldsInitialized').filter(v=>!!v).take(1),
				other => other
			).subscribe((other) => {
				if (!other.fields[desc.codomain.keyInResource]) {
					debugger;
				}
				other.fields[desc.codomain.keyInResource].add(owner);
			});
			this.get().e('delete').subscribe((other) => {
				other.fields[desc.codomain.keyInResource].delete(owner);
			});

			/* mirror stuff that happens in sub-fields */
			owner.p('fieldsInitialized').filter(v=>!!v).take(1).subscribe(() => {
				for (let subCls of desc.relationshipClass.extendedBy) {
					const subFieldKey = subCls.keyInResource[desc.keyInRelationship];
					const subField    = owner.fields[subFieldKey];
					if (!subField) { continue }
					if (subField instanceof Rel$Field) {
						subField.get().e('add')   .subscribe( this.get().e('add')    );
						subField.get().e('delete').subscribe( this.get().e('delete') );
					} else { // Rel1Field
						subField.p('value')
							.startWith(null)
							.pairwise()
							.subscribe(([prev, curr]) => {
								if (prev) { this.get().delete(prev) }
								if (curr) { this.get().add   (curr) }
							});
					}
				}

			});

			/* emit 'value' signals (but note that setValueThroughSignal = false) */
			this[$$value].p('value')
				.sample(owner.p('fieldsInitialized').filter(v=>!!v))
				.subscribe( this.p('value') );

		});
	}

	getAll() {
		return new Set(this.get());
	}

	set(newValue, { ignoreReadonly = false, ignoreValidation = false } = {}) {
		constraint(ignoreReadonly || !this[$$desc].readonly);
		newValue = new Set(newValue);
		if (!ignoreValidation) { this.validate(newValue, ['set']) }
		this[$$value].overwrite(newValue);
	}

	add(newSubValue, { ignoreReadonly = false, ignoreValidation = false } = {}) {
		constraint(ignoreReadonly || !this[$$desc].readonly);
		if (!ignoreValidation) {
			this.validateCardinality(this[$$value]::size() + 1, ['set']);
			this.validateElement(newSubValue, ['set']);
		}
		this.get().add(newSubValue);
	}

	delete(subValue, { ignoreReadonly = false, ignoreValidation = false } = {}) {
		constraint(ignoreReadonly || !this[$$desc].readonly);
		if (!ignoreValidation) {
			this.validateCardinality(this[$$value]::size() - 1, ['set']);
		}
		this.get().delete(subValue);
	}

	static valueToJSON(value, {requireClass, ...options} = {}) {
		return [...value].map(e => {
			if (requireClass && requireClass !== e.class) { return undefined }
			return env.Entity.normalizeAddress(e, options);
		}).filter(v=>!!v);
	}

	static jsonToValue(json, options = {}) {
		let result = new Set;
		const {getEntity} = options;
		for (let thing of json) {
			let entity;
			if (thing instanceof env.Entity) {
				entity = thing;
			} else if (getEntity::isFunction()) {
				entity = getEntity(thing, options);
			}
			result.add(entity);
		}
		return result;
	}

	[$$destruct]() {
		this.set(new Set(), {
			ignoreReadonly:    true,
			ignoreValidation:  true,
			createEditCommand: false
		});
		super[$$destruct]();
	}

	validate(val, stages = []) {
		constraint(val[Symbol.iterator], humanMsg`
			The value ${val} given for ${this[$$owner].constructor.name}#${this[$$key]}
			is not an iterable collection (like array or set).
		`);
		this.validateCardinality(val::size(), stages);
		val.forEach(::this.validateElement, stages);
	}

	validateCardinality(cardinality, stages = []) {
		if (stages.includes('commit')) {
			const {min, max} = this[$$desc].cardinality;
			constraint(cardinality::inRange(min, max + 1), humanMsg`
				The number of values in field ${this[$$owner].constructor.name}#${this[$$key]}
				is not within the expected range [${min}, ${max}].
			`);
		}
	}

	validateElement(element, stages = []) {
		/* the value must be of the proper domain */
		if (!this[$$desc].codomain.resourceClass.hasInstance(element)) {
			throw new Error(humanMsg`
				Invalid value ${element} given as element for
				${this[$$owner].constructor.name}#${this[$$key]}.
			`);
		}
	}

});

import {get, isUndefined, isNull, entries, isFunction} from 'lodash-bound';

import {defineProperty} from 'bound-native-methods';

import assert from 'power-assert';

import {humanMsg, callOrReturn} from 'utilities';

import {constraint} from "../util/misc";

import RelField_factory from './RelField.js';

import {
	$$owner,
	$$key,
	$$desc,
	$$initSet,
	$$entriesIn,
	$$destruct
} from './symbols';


/** @private */
export default (env) => env.registerFieldClass('Rel1Field', class Rel1Field extends RelField_factory(env) {

	// this[$$owner] instanceof Resource
	// this[$$key]   instanceof "-->HasInnerBorder" | "<--HasPlusBorder" | ...
	// this[$$value] instanceof IsRelatedTo

	////////////
	// static //
	////////////

	static initClass({ cls, key, aliases, desc: {readonly} }) {
		assert(cls.isResource);
		if (cls.prototype.hasOwnProperty(key)) { return }
		for (let k of [key, ...aliases]) {
			cls.prototype::defineProperty(k, {
				get() { return this.fields[k].get() },
				...(readonly ? {} : {
					set(val) { this.fields[k].set(val)}
				}),
				enumerable:   true,
				configurable: false
			});
		}
	}

	static [$$entriesIn](cls) {
		if (!cls.isResource) { return [] }
		return cls.relationships::entries()
             .filter(([,desc]) => desc.cardinality.max === 1)
             .map(([key, desc]) => ({
                 key,
                 desc,
	             aliases: desc.shortcutKey ? [desc.shortcutKey] : []
             }));
	}


	//////////////
	// instance //
	//////////////

	constructor(options) {
		super(options);
		const { owner, key, desc, initialValue, related } = options;

		this.p('isPlaceholder').filter(v=>!v).take(1).subscribe(() => {

			/* set the initial value */
			const initialShortcutValue = related::get([desc.shortcutKey, 'initialValue']);
			constraint(!initialValue || !initialShortcutValue, humanMsg`
				You cannot set the fields '${key}' and '${desc.shortcutKey}'
				at the same time for a ${this.constructor.singular}.
			`);
			this[$$initSet](
				[!initialValue        ::isUndefined(), () => initialValue        ::callOrReturn(owner)],
				[!initialShortcutValue::isUndefined(), () => initialShortcutValue::callOrReturn(owner)],
				[desc.cardinality.min === 0,           () => null]
			);

			/* synchronize with the other side */
			this.p('value').startWith(null).distinctUntilChanged().pairwise().subscribe(([prev, curr]) => {
				if (prev) { prev.fields[desc.codomain.keyInResource].delete(owner) }
				if (curr) { curr.fields[desc.codomain.keyInResource].add   (owner) }
			});

			/* pull in values set in sub-fields */
			owner.p('fieldsInitialized').filter(v=>!!v).take(1).subscribe(() => {
				for (let subCls of desc.relationshipClass.extendedBy) {
					const subFieldKey = subCls.keyInResource[desc.keyInRelationship];
					const subField = owner.fields[subFieldKey];
					if (!subField) { continue }
					subField.p('value').subscribe( this.p('value') );
				}
			});

		});
	}

	static valueToJSON(value, {requireClass, ...options} = {}) {
		if (!value) { return value }
		if (requireClass && requireClass !== value.class) { return undefined }
		return env.Entity.normalizeAddress(value, options);
	}

	static jsonToValue(json, options = {}) {
		if (json === null) { return null }
		const {getEntity} = options;
		if (json instanceof env.Entity) {
			return json;
		} else if (getEntity::isFunction()) {
			return getEntity(json);
		}
	}

	[$$destruct]() {
		this.set(null, {
			ignoreReadonly:   true,
			ignoreValidation: true
		});
		super[$$destruct]();
	}

	getAll() {
		let val = this.get();
		return new Set(val === null ? [] : [val]);
	}

	add(newValue, options) {
		this.set(newValue, options);
	}

	delete(oldValue, options) {
		if (this.get() === oldValue) {
			this.set(null, options);
		}
	}

	validate(val, stages = []) {

		const notGiven = val::isNull() || val::isUndefined();

		if (stages.includes('commit')) {
			/* if there's a minimum cardinality, a value must have been given */
			constraint(!notGiven || this[$$desc].cardinality.min === 0, humanMsg`
				No value given for required field
				${this[$$owner].constructor.name}#${this[$$key]}.
			`);
		}

		/* the value must be of the proper domain */
		const expectedResourceClass = this[$$desc].codomain.resourceClass;
		const hasCompatibleType = expectedResourceClass.hasInstance(val);
		constraint(notGiven || hasCompatibleType, humanMsg`
			Invalid value '${val}' given for field ${this[$$owner].constructor.name}#${this[$$key]}.
		`);

	}

	validateElement(element, options) {
		return this.validate(element, options);
	}

});

