	it("exports classes that can be instantiated", () => {

		const {Material, Lyph} = environment.classes;

		let material = Material.new();
		let lyph     = Lyph.new();
		expect(material).to.be.an.instanceOf(Material);
		expect(lyph    ).to.be.an.instanceOf(Material, Lyph);

