import {defaults, isInteger, isObject, entries, assign} from 'lodash-bound';
import assert from 'power-assert';

import {humanMsg, definePropertiesByValue, babelHelpers, ValueTracker, event,
	property, flag
} from 'utilities';

import {constraint} from './util/misc';

const $$allowInvokingConstructor = Symbol('$$allowInvokingConstructor');

////////////////////////////////////////////////////////////////////////////////

/** @wrapper */
export default (env) => {
	/**
	 * The base-class of all entities described in the manifest.
	 * @public
	 */
	class Entity extends ValueTracker {

		static get environment() { return env }

		static get Entity() { return Entity }

		static normalizeAddress(address, options = {}) {
			const {entityToTemporaryId = new Map} = options;
			if (address::isInteger()) { return { class: this.name, id: address } }
			if (address::isObject()) {
				let id = address.id || entityToTemporaryId.get(address);
				return { class: address.class || this.name, id };
			}
			assert(false, humanMsg`${JSON.stringify(address)} is not a valid entity identifier.`);
		}

		////////////////////////////////////////////////////////////
		////////// STATIC (building Entity-based classes) //////////
		////////////////////////////////////////////////////////////

		/**
		 * Create a new `Entity` subclass with the given configuration.
		 * @param {Object} config      - a set of static properties for the new class
		 * @param {string} config.name - the name of the new class
		 * @return {Class} a new subclass of `Entity`
		 */
		static createClass(config): Class<Entity> {
			/* create the class with the right name, constructor and static content */
			const {name, ...rest} = config;

			/* create the new class */
			// using Function constructor to give the class a dynamic name
			// http://stackoverflow.com/a/9947842/681588
			// (and using babel-technique to build it, rather than using class
			//  expression, so that it can be extended by babel-compiled code)
			const EntitySubclass = new Function('Entity', `
				'use strict';
				${babelHelpers};
				return function (_Entity) {
					_inherits(${name}, _Entity);
					function ${name}() {
						_classCallCheck(this, ${name});
						return _possibleConstructorReturn(this, Object.getPrototypeOf(${name}).apply(this, arguments));
					}
					return ${name};
				}(Entity);
			`)(Entity);
			EntitySubclass::definePropertiesByValue({ name });

			/* populate it with the requested data and behavior */
			EntitySubclass::assign(rest);

			/***/
			return EntitySubclass;
		}



		///////////////////////////////////////////////////
		////////// STATIC CLASS ANALYSIS METHODS //////////
		///////////////////////////////////////////////////

		/**
		 * Test whether the given entity is an instance of this class.
		 * This is a polymorphic check; call it on `Entity` subclasses.
		 * @param {Entity} entity - the entity to test
		 * @return {boolean} whether the given entity is an instance of this class
		 */
		static hasInstance(entity) {
			if (!entity) { return false }
			return this.hasSubclass(entity.constructor);
		}

		/**
		 * Test whether the given class is a subclass of this class.
		 * This is a polymorphic check; call it on `Entity` subclasses.
		 * @param {Class} otherClass - the class to test
		 * @return {boolean} whether the given class is subclass of this class
		 */
		static hasSubclass(otherClass) {
			if (!otherClass || otherClass.Entity !== this.Entity) { return false }
			if (this === this.Entity)                             { return true  }
			if (otherClass === this)                              { return true  }
			for (let SubClass of this.extendedBy) {
				if (SubClass.hasSubclass(otherClass)) {
					return true;
				}
			}
			return false;
		}

		/**
		 * Returns all the subclasses of this class, both direct and indirect.
		 * This is a polymorphic check; call it on `Entity` subclasses.
		 * @return {Set<Class>} the set of subclasses of this class
		 */
		static allSubclasses() {
			let result = [this];
			for (let subClass of this.extendedBy) {
				result = [...result, ...subClass.allSubclasses()];
			}
			return new Set(result);
		}

		/**
		 * Overwriting the JavaScript `instanceof` operator to work with our improvised class hierarchy.
		 * @param {Entity} entity - the entity to test
		 * @return {boolean} whether the given entity is an instance of this class
		 * @see Entity.hasInstance
		 */
		static [Symbol.hasInstance](entity) {
			return this.hasInstance(entity);
		}


		///////////////////////////////
		////////// INSTANCES //////////
		///////////////////////////////

		/**
		 * Create a new entity of this (polymorphically determined) Entity subclass.
		 * @param initialValues
		 * @param options
		 * @return {Entity}
		 */
		static new(
			initialValues: {} = {},
		    options:       {} = {}
		) : this {
			if (this.isRelationship) {
				throw new Error(humanMsg`
					You cannot instantiate ${this.name},
					because it is a Relationship subclass.
				`);
			}
			return new this(initialValues, {
				...options,
				[$$allowInvokingConstructor]: true
			});
		}


		@property({ readonly: true }) isPlaceholder;
		@property({ initial: false, readonly: true }) fieldsInitialized;
		@property({ initial: false, allowSynchronousAccess: true, isValid(v) { return v === false || !this.isPlaceholder } }) deleted;
		@property({ initial: false, allowSynchronousAccess: true, isValid(v) { return v === false || !this.isPlaceholder } }) silent;


		constructor(initialValues = {}, options = {}) {
			/* initialize value tracking */
			super();

			/* make sure this constructor was invoked under proper conditions */
			constraint(options[$$allowInvokingConstructor], humanMsg`
				Do not use 'new ${this.constructor.name}(...args)'.
				Instead, use '${this.constructor.name}.new(...args)'
				          or '${this.constructor.name}.get(...args)'.
			`);
			delete options[$$allowInvokingConstructor];

			/* init placeholder property */
			this.pSubject('isPlaceholder').next(options.isPlaceholder);

			/* stop signals after this entity is deleted */
			const valueTrackerOptions = {
				takeUntil: this.p('silent').filter(s=>!!s)
			};
			this.setValueTrackerOptions(valueTrackerOptions);

			/* set defaults for the core initial field values */
			initialValues::defaults({
				class: this.constructor.name
			});

			/* initialize all fields in this entity */
			this.constructor.environment.Field.initializeEntity(this, initialValues, valueTrackerOptions);
		}

		loadIntoPlaceholder(initialValues = {}) {
			//assert(this.isPlaceholder); // TODO: fix ValueTracker, so that this.isPlaceholder is available
			for (let [key, value] of initialValues::entries()) {
				if (!this.fields[key]) { continue }
				this.fields[key].set(value);
			}
			this.pSubject('isPlaceholder').next(false);
		}

		get [Symbol.toStringTag]() {
			const identifier = this.id || this.name || this.title; // <-- just some common candidates
			return `${this.constructor.name}${identifier ? ': '+identifier : ''}`;
		}

		p(...args) {
			if (!!this.fields && !!this.fields[args[0]]) {
				return this.fields[args[0]].p('value');
			} else if (this.hasProperty(...args)) {
				return super.p(...args);
			}
		}


		//// Deleting
		delete  () { this.pSubject('deleted').next(true)  }
		undelete() { this.pSubject('deleted').next(false) }

		silence() { this.pSubject('silent').next(true) }


		//// Transforming to/from JSON

		static objectToJSON(obj, options = {}) {
			let { sourceEntity } = options;
			// TODO: rather than sourceEntity, accept an entity CLASS,
			//     : which should have a good description of the fields
			//     : This is issue open-physiology/open-physiology-model#10
			let result = {};
			for (let [key, value] of obj::entries()) {
				const field = sourceEntity.fields[key];
				let opts;
				switch (field.constructor.name) {
					case 'Rel$Field': case 'Rel1Field': {
						opts = { ...options, requireClass: key.substring(3) };
					} break;
					case 'SideField': case 'PropertyField': {
						opts = options;
					} break;
					default: continue; // Don't put shortcut fields in JSON
				}
				let valueJSON = field.constructor.valueToJSON(value, opts);
				if (field.constructor.name === 'Rel$Field' && valueJSON.length === 0) { continue }
				if (field.constructor.name === 'Rel1Field' && valueJSON === null)     { continue }
				result[key] = valueJSON;
			}
			return result;
		}

		toJSON(options = {}) {
			let result = {};
			for (let [key, field] of this.fields::entries()) {
				result[key] = field.value;
			}
			return this.constructor.objectToJSON(result, { ...options, sourceEntity: this });
		}


		//// Setting / getting of fields

		get(key)                    { return this.fields[key].get()             }
		set(key, val, options = {}) { return this.fields[key].set(val, options) }

	}

	return env.Entity || Entity;
};

import {pick, mapValues, isUndefined, values} from 'lodash-bound';

import {defineProperty} from 'bound-native-methods';

import assert from 'power-assert';

import {ValueTracker, property, callOrReturn, humanMsg} from 'utilities';

import {
	$$owner,
	$$key,
	$$desc,
	$$value,
	$$initSet,
	$$entriesIn
} from './symbols';
const $$fieldsInitialized = Symbol('$$fieldsInitialized');
const $$aliases           = Symbol('$$aliases');

import {constraint} from "../util/misc";

/** @wrapper */
export default (env) => {
	/**
	 * A class that represents specific fields on an `Entity`.
	 * @public
	 */
	class Field extends ValueTracker {

		////////////
		// static //
		////////////

		/**
		 * Equip an `Entity` subclass `cls` with its particular fields,
		 * optionally only for a particular key.
		 * @param {Class}   cls         - a subclass of `Entity` still under construction
		 * @param {string} [onlyForKey] - the only key for which to initialize the field
		 */
		static augmentClass(cls, onlyForKey) {
			/* allow each kind of field to perform its initializations */
			for (let FieldClass of env.fieldClasses::values()) {
				if (!FieldClass[$$entriesIn]) { continue }
				for (let { key, desc, aliases } of FieldClass[$$entriesIn](cls)) {
					if (!onlyForKey || onlyForKey === key) {
						FieldClass.initClass({ cls, key, aliases, desc });
					}
				}
			}
		}

		/**
		 * Initialize the fields of a particular `Entity` instance.
		 * @param {Entity} owner                    - the entity to initialize
		 * @param {Object} initialValues            - the initial values for the entity's fields
		 * @param {Object} [valueTrackerOptions={}] - the `ValueTracker` options to use for each field's observable properties
		 */
		static initializeEntity(owner, initialValues, valueTrackerOptions = {}) {
			if (owner.fields) { return }
			owner::defineProperty('fields', { value: {} });

			/* initialize all fields */
			const keyDescs = {};
			for (let FieldClass of env.fieldClasses::values()) {
				if (!FieldClass[$$entriesIn]) { continue }
				for (let entry of FieldClass[$$entriesIn](owner.constructor)) {
					const { key, aliases = [] } = entry;
					const candidateKeys  = [key, ...aliases].filter(v => !initialValues[v]::isUndefined());
					constraint(candidateKeys.length <= 1, humanMsg`
						You cannot set the fields '${candidateKeys.join("', '")}'
						at the same time for a ${owner.constructor.singular}.
					`);
					keyDescs[key] = {
						...entry,
						owner,
						key,
						initialValue: initialValues[candidateKeys[0]],
						FieldClass
					};
				}
			}

			/* add related initial values to each description */
			for (let entry of keyDescs::values()) {
				entry.aliasInitialValues = keyDescs::pick(entry.relatedKeys)::mapValues(d => d.initialValue);
			}

			/* create a field for each description */
			for (let entry of keyDescs::values()) {
				let { FieldClass } = entry;
				delete entry.FieldClass;
				new FieldClass({ ...entry, valueTrackerOptions });
			}

			/* notify completion of field initialization */
			owner.pSubject('fieldsInitialized').next(true);
		}

		/**
		 * The function used for testing the values of this field for equality.
		 * Can be overwritten by specific field classes (e.g., `Rel$Field`).
		 * @returns {boolean} - whether `a` and `b` are considered equal values
		 */
		static isEqual(a, b) { return a === b }


		/////////////////////////
		// events & properties //
		/////////////////////////

		/**
		 * The current value of this field. It can be read and written to
		 * directly, or observed with `field.p('value')`.
		 */
		@property() value;


		//////////////
		// instance //
		//////////////

		/**
		 * Making this field recognizable when converted to a string.
		 * @returns {string} a string like `Field: Lyph#name = "Heart"`
		 */
		get [Symbol.toStringTag]() {
			return `Field: ${this[$$owner].constructor.name}#${this[$$key]} = ${this.valueToJSON()}`;
		}

		/**
		 * Create a new instance of this field on a specific entity.
		 * @protected
		 * @param {Object}    options
		 * @param {Entity}    options.owner                       - the entity on which to create this field
		 * @param {string}    options.key                         - the key on that entity corresponding to this field
		 * @param {Object}    options.desc                        - the descriptor for what kind of field this is
		 * @param {Iterable} [options.aliases=[]]                 - other keys to which this field should answer
		 * @param {boolean}  [options.setValueThroughSignal=true] - whether signals sent to `field.p('value')` should be accepted
		 * @param {Object}   [options.valueTrackerOptions={}]     - the `ValueTracker` options to use for each field's observable properties
		 */
		constructor(options) {
			super();
			this.setValueTrackerOptions(options.valueTrackerOptions);
			const {
				owner,
				key,
				desc,
				aliases = [],
				setValueThroughSignal = true
			} = options;
			owner.fields[key] = this;
			for (let alias of aliases) {
				owner.fields[alias] = this;
			}
			/** @private */ this[$$owner]   = owner;
			/** @private */ this[$$key]     = key;
			/** @private */ this[$$desc]    = desc;
			/** @private */ this[$$aliases] = aliases;
			if (setValueThroughSignal) {
				this.p('value').subscribe(::this.set);
			}
		}

		/**
		 * Convert this field's value to JSON (plain data).
		 * @param {Object} [options={}]
		 * @returns {*}
		 */
		valueToJSON(options = {}) { return this.constructor.valueToJSON(this.value, options) }

		/**
		 * How to properly convert a value in this field to JSON (plain data).
		 * @param {*}        value                             - the value to convert to JSON
		 * @param {Object}  [options={}]
		 * @param {boolean} [options.flattenFieldValues=false] - whether `Object` or `Array` field values should be avoided in the output (for example, Neo4j does not accept nested objects)
		 * @abstract
		 */
		static valueToJSON(value, options) {
			assert(false, humanMsg`Field.valueToJSON must be implemented in subclasses.`);
		}

		/**
		 * How to properly convert a JSON value to a value appropriate for this type of field.
		 * @param {*}         json              - the JSON value to convert
		 * @param {Object}    options
		 * @param {boolean}  [options.flattenFieldValues=false] - whether field values are strings that should be run through `JSON.parse`
		 * @param {Function}  options.getEntity                 - a function that returns an entity corresponding to a given address
		 */
		static jsonToValue(json, options) {
			assert(false, humanMsg`Field.jsonToValue must be implemented in subclasses.`);
		}

		/**
		 * @returns {*} this field's value
		 */
		get() { return this[$$value] }

		/**
		 * Set this field to a new value.
		 * @param newValue - the new value for this field
		 * @param {Object}  [options={}]
		 * @param {boolean} [options.ignoreReadonly=false]   - allow this field's value to be changed even if it is read-only
		 * @param {boolean} [options.ignoreValidation=false] - don't validate the new value
		 */
		set(newValue, options = {}) {
			if (!this.constructor.isEqual(this[$$value], newValue)) {
				const {
			        ignoreReadonly   = false,
			        ignoreValidation = false
		        } = options;
				constraint(ignoreReadonly || !this[$$desc].readonly, humanMsg`
					Tried to set the readonly field
					'${this[$$owner].constructor.name}#${this[$$key]}'.
				`);
				if (!ignoreValidation) { this.validate(newValue, ['set']) }
				/** @private */ this[$$value] = newValue;
				this.pSubject('value').next(newValue);
			}
		}

		/**
		 * Validate a certain value as appropriate for this field.
		 * Throws an exception if the value is invalid. Otherwise does nothing.
		 * Should be overwritten by specific `Field` subclasses.
		 * @param {*}     val    - the value to test
		 * @param {Array} stages - the validation stages to validate for (e.g., 'commit')
		 */
		validate(val, stages = []) {
		} // to be implemented in subclasses

		/** @private */
		[$$initSet](...alternatives) {
			for (let [guard, value] of alternatives) {
				if (guard::callOrReturn(this)) {
					if (value::isUndefined()) { return }
					value = value::callOrReturn();
					this.validate(value, ['initialize', 'set']);
					this.set(value, {
						ignoreReadonly:   true,
						ignoreValidation: true
					});
					return;
				}
			}
		}

	}

	return env.registerFieldClass('Field', Field);
}

import {isUndefined, entries, cloneDeep} from 'lodash-bound';
import {defineProperty}                  from 'bound-native-methods';
import {humanMsg, callOrReturn}          from 'utilities';
import {constraint} from "../util/misc";
import Field_factory from './Field.js';
import {
	$$owner,
	$$key,
	$$desc,
	$$initSet,
	$$entriesIn
} from './symbols';

const $$ignoreReadonly = Symbol('$$ignoreReadonly');


/** @wrapper */
export default (env) => {

	const Field = Field_factory(env);

	/**
	 * A field on an `Entity` representing a simple data-type.
	 */
	class PropertyField extends Field {

		// this[$$owner]   instanceof   RelatedTo | Resource
		// this[$$key]     instanceof   "name" | "class" | "id" | ...
		// this[$$value]   instanceof   any

		////////////
		// static //
		////////////

		/**
		 * Equip an `Entity` subclass `cls` with a particular property field.
		 * @param {Object}   options
		 * @param {Class}    options.cls - a subclass of `Entity` still under construction
		 * @param {string}   options.key - the key for the field to put in
		 * @param {Object}  [options.desc]
		 * @param {boolean} [options.desc.readonly] - whether this field should be read-only
		 */
		static initClass(options) {
			const {cls, key, desc} = options;
			if (cls.prototype.hasOwnProperty(key)) { return }
			const {readonly} = desc;
			cls.prototype::defineProperty(key, {
				get() { return this.fields[key].get() },
				...(readonly ? {} : {
					set(val) { this.fields[key].set(val)}
				}),
				enumerable:   true,
				configurable: false
			});
		}

		/** @private */
		static [$$entriesIn](cls) {
			return cls.properties::entries()
				.map(([key, desc])=>({
					key,
					desc
				}));
		}


		//////////////
		// instance //
		//////////////

		/**
		 * Create a new instance of this field on a specific entity.
		 * @protected
		 * @param {Object}    options
		 * @param {Entity}    options.owner                       - the entity on which to create this field
		 * @param {string}    options.key                         - the key on that entity corresponding to this field
		 * @param {Object}    options.desc                        - the descriptor for what kind of field this is
		 * @param {string}   [options.initialValue]               - the initial value for this field
		 * @param {Iterable} [options.aliases=[]]                 - other keys to which this field should answer
		 * @param {boolean}  [options.setValueThroughSignal=true] - whether signals sent to `field.p('value')` should be accepted
		 * @param {Object}   [options.valueTrackerOptions={}]     - the `ValueTracker` options to use for each field's observable properties
		 */
		constructor(options) {
			super(options);
			const { owner, desc, initialValue } = options;

			/* set the initial value */
			this[$$initSet](
				[!initialValue::isUndefined(), () => initialValue::callOrReturn(owner)::cloneDeep()],
				['default' in desc,            () => desc.default::callOrReturn(owner)::cloneDeep()],
				['value'   in desc,            () => desc.value  ::callOrReturn(owner)::cloneDeep()],
				[!desc.required]
			);
		}

		/**
		 * How to properly convert a value in this field to JSON (plain data).
		 * @param {*}        value                             - the value to convert to JSON
		 * @param {Object}  [options={}]
		 * @param {boolean} [options.flattenFieldValues=false] - whether `Object` or `Array` field values should be avoided in the output (for example, Neo4j does not accept nested objects)
		 * @returns {*} a plain-data value representing `value` that can go inside a JSON object
		 */
		static valueToJSON(value, options = {}) {
			const {flattenFieldValues = false} = options;
			if (flattenFieldValues) { value = JSON.stringify(value) }
			return value;
		}

		/**
		 * How to properly convert a JSON value to a value appropriate for this type of field.
		 * @param {*}        json                              - the JSON value to convert
		 * @param {Object}  [options={}]
		 * @param {boolean} [options.flattenFieldValues=false] - whether field values are strings that should be run through `JSON.parse`
		 */
		static jsonToValue(json, options = {}) {
			const {flattenFieldValues = false} = options;
			if (flattenFieldValues) { json = JSON.parse(value) }
			return json;
		}

		/**
		 * Validate a certain value as appropriate for this field.
		 * Throws an exception if the value is invalid. Otherwise does nothing.
		 * @param {*}     val    - the value to test
		 * @param {Array} stages - the validation stages to validate for (e.g., 'commit')
		 */
		validate(val, stages = []) {

			if (stages.includes('commit')) {
				constraint(!this[$$desc].required || !val::isUndefined(), humanMsg`
				    No value given for required field
				    '${this[$$owner].constructor.name}#${this[$$key]}'.
				`);
			}

			// TODO: CHECK CONSTRAINT: given property value conforms to JSON schema
			// TODO: CHECK ADDITIONAL (PROPERTY-SPECIFIC) CONSTRAINTS:
			//     : e.g., if this is a template, does it conform to the specs of its corresponding type?

		}

	}

	return env.registerFieldClass('PropertyField', PropertyField);
};

import {inRange, size, entries, isFunction} from 'lodash-bound';

import {defineProperty} from 'bound-native-methods';

import assert from 'power-assert';

import ObservableSet from '../util/ObservableSet';
import {humanMsg, callOrReturn} from 'utilities';
import {constraint, setEquals} from '../util/misc';

import RelField_factory from './RelField.js';

import {
	$$owner,
	$$key,
	$$desc,
	$$value,
	$$entriesIn,
	$$initSet
} from './symbols';


/** @wrapper */
export default (env) => {

	const RelField = RelField_factory(env);

	/**
	 * A field on an `Entity` representing the 'many'-side
	 * of a many-to-many or many-to-one relationship.
	 * Therefore, its value is always a `Set`.
	 */
	class Rel$Field extends RelField {

		// this[$$owner] instanceof Resource
		// this[$$key]   instanceof "-->ContainsMaterial" | "-->HasPart" | "<--FlowsTo" | ...
		// this[$$value] instanceof Set<IsRelatedTo>

		////////////
		// static //
		////////////

		/**
		 * Equip an `Entity` subclass `cls` with a particular property field.
		 * @param {Object}   options
		 * @param {Class}    options.cls     - a subclass of `Entity` still under construction
		 * @param {string}   options.key     - the key for the field to put in
		 * @param {string}   options.aliases - additional keys for this field
		 * @param {Object}  [options.desc]
		 * @param {boolean} [options.desc.readonly] - whether this field should be read-only
		 */
		static initClass(options) {
			const { cls, key, aliases, desc: {readonly} } = options;
			assert(cls.isResource);
			if (cls.prototype.hasOwnProperty(key)) { return }
			for (let k of [key, ...aliases]) {
				cls.prototype::defineProperty(k, {
					get() { return this.fields[k].get() },
					...(readonly ? {} : {
						set(val) { this.fields[k].set(val)}
					}),
					enumerable:   true,
					configurable: false
				});
			}
		}

		/** @private */
		static [$$entriesIn](cls) {
			if (!cls.isResource) { return [] }
			return cls.relationships::entries()
				.filter(([,rel]) => rel.cardinality.max > 1)
				.map(([key, desc]) => ({
					key,
					desc,
					aliases: desc.shortcutKey ? [desc.shortcutKey] : []
				}));
		}

		/**
		 * The function used for testing the values of this field for set-equality.
		 * @param a
		 * @param b
		 * @returns {boolean}
		 */
		static isEqual = setEquals;


		//////////////
		// instance //
		//////////////

		/**
		 * Create a new instance of this field on a specific entity.
		 * @protected
		 * @param {Object}    options
		 * @param {Entity}    options.owner                       - the entity on which to create this field
		 * @param {string}    options.key                         - the key on that entity corresponding to this field
		 * @param {Object}    options.desc                        - the descriptor for what kind of field this is
		 * @param {string}   [options.initialValue]               - the initial value for this field
		 * @param {Iterable} [options.aliases=[]]                 - other keys to which this field should answer
		 * @param {Object}   [options.aliasInitialValues=[]]      - other keys to which this field should answer
		 * @param {boolean}  [options.setValueThroughSignal=true] - whether signals sent to `field.p('value')` should be accepted
		 * @param {Object}   [options.valueTrackerOptions={}]     - the `ValueTracker` options to use for each field's observable properties
		 */
		constructor(options) {
			super({ ...options, setValueThroughSignal: false });
			const {owner, desc, initialValue, aliasInitialValues} = options;

			/* initialize an empty observable set */
			this::defineProperty($$value, { value: new ObservableSet });

			/* set the initial value */
			const initialScValue = aliasInitialValues[desc.shortcutKey];
			constraint(!initialValue || !initialScValue, humanMsg`
				You cannot set the fields '${desc.keyInResource}' and '${desc.shortcutKey}'
				at the same time for a ${this.constructor.singular}.
			`);
			this[$$initSet](
				[initialValue   && initialValue  [Symbol.iterator], () => initialValue  ::callOrReturn(owner)],
				[initialScValue && initialScValue[Symbol.iterator], () => initialScValue::callOrReturn(owner)],
				[desc.cardinality.min === 0]
			);

			owner.p('fieldsInitialized').filter(v=>!!v).take(1).subscribe(() => {

				/* synchronize with the other side */
				this.get().e('add').switchMap(
					other => other.p('fieldsInitialized').filter(v=>!!v).take(1),
					other => other
				).subscribe((other) => {
					if (!other.fields[desc.codomain.keyInResource]) {
						debugger;
					}
					other.fields[desc.codomain.keyInResource].add(owner);
				});
				this.get().e('delete').subscribe((other) => {
					other.fields[desc.codomain.keyInResource].delete(owner);
				});

				/* mirror stuff that happens in sub-fields */
				for (let subCls of desc.relationshipClass.extendedBy) {
					const subFieldKey = subCls.keyInResource[desc.keyInRelationship];
					const subField    = owner.fields[subFieldKey];
					if (!subField) { continue }
					if (subField instanceof Rel$Field) {
						subField.get().e('add')   .subscribe( this.get().e('add')    );
						subField.get().e('delete').subscribe( this.get().e('delete') );
					} else { // Rel1Field
						subField.p('value')
							.startWith(null)
							.pairwise()
							.subscribe(([prev, curr]) => {
								if (prev) { this.get().delete(prev) }
								if (curr) { this.get().add   (curr) }
							});
					}
				}

			});

			/* emit 'value' signals (but note that setValueThroughSignal = false) */
			this[$$value].p('value')
				.sample(owner.p('fieldsInitialized').filter(v=>!!v))
				.subscribe( this.p('value') );

		}

		/**
		 * @return {Set} A new `Set` data-structure populated with
		 *               the entities in this field.
		 */
		getAll() {
			return new Set(this.get());
		}

		/**
		 * Set this field to a new collection of related entities.
		 * @param {Iterable} newValue - the new collection of entities to be put in this field
		 * @param {Object}  [options={}]
		 * @param {boolean} [options.ignoreReadonly=false]   - allow this field's value to be changed even if it is read-only
		 * @param {boolean} [options.ignoreValidation=false] - don't validate the new value
		 */
		set(newValue, options = {}) {
			const {ignoreReadonly = false, ignoreValidation = false} = options;
			constraint(ignoreReadonly || !this[$$desc].readonly);
			newValue = new Set(newValue);
			if (!ignoreValidation) { this.validate(newValue, ['set']) }
			this[$$value].overwrite(newValue);
		}

		/**
		 * Add an additional entity to this field.
		 * @param {Entity}   newSubValue - the new entity to be added into this field
		 * @param {Object}  [options={}]
		 * @param {boolean} [options.ignoreReadonly=false]   - allow this field's value to be changed even if it is read-only
		 * @param {boolean} [options.ignoreValidation=false] - don't validate the new cardinality or value
		 */
		add(newSubValue, options = {}) {
			const {ignoreReadonly = false, ignoreValidation = false} = options;
			constraint(ignoreReadonly || !this[$$desc].readonly);
			if (!ignoreValidation) {
				this.validateCardinality(this[$$value]::size() + 1, ['set']);
				this.validateElement(newSubValue, ['set']);
			}
			this.get().add(newSubValue);
		}

		/**
		 * Remove a specific entity from this field.
		 * @param {Entity}   subValue - the entity to be removed from this field
		 * @param {Object}  [options={}]
		 * @param {boolean} [options.ignoreReadonly=false]   - allow this field's value to be changed even if it is read-only
		 * @param {boolean} [options.ignoreValidation=false] - don't validate the new cardinality
		 */
		delete(subValue, options = {}) {
			const {ignoreReadonly = false, ignoreValidation = false} = options;
			constraint(ignoreReadonly || !this[$$desc].readonly);
			if (!ignoreValidation) {
				if (this[$$value].has(subValue)) {
					this.validateCardinality(this[$$value]::size() - 1, ['set']);
				}
			}
			this.get().delete(subValue);
		}

		/**
		 * How to properly convert a value in this field to JSON (plain data).
		 * @param {*}       value                 - the value to convert to JSON
		 * @param {Object} [options={}]
		 * @param {Class}  [options.requireClass] - the only `Entity` subclass accepted into the output, optionally
		 */
		static valueToJSON(value, options = {}) {
			let requireClass;
			({requireClass, ...options} = options);
			return [...value].map(e => {
				if (requireClass && requireClass !== e.class) { return undefined }
				return env.Entity.normalizeAddress(e, options);
			}).filter(v=>!!v);
		}

		/**
		 * How to properly convert a JSON value to a value appropriate for this type of field.
		 * @param {*}         json               - the JSON value to convert
		 * @param {Object}   [options={}]
		 * @param {Function} [options.getEntity] - a function that returns an entity corresponding to a given address
		 */
		static jsonToValue(json, options = {}) {
			let result = new Set;
			const {getEntity} = options;
			for (let thing of json) {
				let entity;
				if (thing instanceof env.Entity) {
					entity = thing;
				} else if (getEntity::isFunction()) {
					entity = getEntity(thing, options);
				}
				result.add(entity);
			}
			return result;
		}

		/**
		 * Validate a certain value as appropriate for this field.
		 * Throws an exception if the value is invalid. Otherwise does nothing.
		 * @param {*}     val    - the value to test
		 * @param {Array} stages - the validation stages to validate for (e.g., 'commit')
		 */
		validate(val, stages = []) {
			constraint(val[Symbol.iterator], humanMsg`
				The value ${val} given for ${this[$$owner].constructor.name}#${this[$$key]}
				is not an iterable collection (like array or set).
			`);
			this.validateCardinality(val::size(), stages);
			val.forEach(::this.validateElement, stages);
		}

		/**
		 * Validate a certain cardinality as appropriate for this field.
		 * Throws an exception if the cardinality is invalid. Otherwise does nothing.
		 * @param {number} cardinality - the cardinality to test
		 * @param {Array}  stages      - the validation stages to validate for (e.g., 'commit')
		 */
		validateCardinality(cardinality, stages = []) {
			if (stages.includes('commit')) {
				const {min, max} = this[$$desc].cardinality;
				constraint(cardinality::inRange(min, max + 1), humanMsg`
					The number of values in field ${this[$$owner].constructor.name}#${this[$$key]}
					is not within the expected range [${min}, ${max}].
				`);
			}
		}

		/**
		 * Validate a specific entity as appropriate to be included in this field.
		 * Throws an exception if the entity is invalid. Otherwise does nothing.
		 * @param {Entity} entity     - the entity to test
		 * @param {Array} [stages=[]] - the validation stages to validate for (e.g., 'commit')
		 */
		validateElement(entity, stages = []) {
			/* the value must be of the proper domain */
			if (!this[$$desc].codomain.resourceClass.hasInstance(entity)) {
				throw new Error(humanMsg`
					Invalid value ${entity} given as element for
					${this[$$owner].constructor.name}#${this[$$key]}.
				`);
			}
		}

	}

	return env.registerFieldClass('Rel$Field', Rel$Field);

}

import {isUndefined, isNull, entries, isFunction} from 'lodash-bound';

import {defineProperty} from 'bound-native-methods';

import assert from 'power-assert';

import {humanMsg, callOrReturn} from 'utilities';

import {constraint} from "../util/misc";

import RelField_factory from './RelField.js';

import {
	$$owner,
	$$key,
	$$desc,
	$$initSet,
	$$entriesIn
} from './symbols';


/** @wrapper */
export default (env) => {

	const RelField = RelField_factory(env);

	/**
	 * A field on an `Entity` representing the 'one'-side
	 * of a one-to-one or one-to-many relationship.
	 * Therefore, its value is always an `Entity` or `null`.
	 */
	class Rel1Field extends RelField {

		// this[$$owner] instanceof Resource
		// this[$$key]   instanceof "-->HasInnerBorder" | "<--HasPlusBorder" | ...
		// this[$$value] instanceof IsRelatedTo

		////////////
		// static //
		////////////

		/**
		 * Equip an `Entity` subclass `cls` with a particular property field.
		 * @param {Object}   options
		 * @param {Class}    options.cls     - a subclass of `Entity` still under construction
		 * @param {string}   options.key     - the key for the field to put in
		 * @param {string}   options.aliases - additional keys for this field
		 * @param {Object}  [options.desc]
		 * @param {boolean} [options.desc.readonly] - whether this field should be read-only
		 */
		static initClass(options) {
			const {cls, key, aliases, desc: {readonly}} = options;
			assert(cls.isResource);
			if (cls.prototype.hasOwnProperty(key)) { return }
			for (let k of [key, ...aliases]) {
				cls.prototype::defineProperty(k, {
					get() { return this.fields[k].get() },
					...(readonly ? {} : {
						set(val) { this.fields[k].set(val) }
					}),
					enumerable:   true,
					configurable: false
				});
			}
		}

		/** @private */
		static [$$entriesIn](cls) {
			if (!cls.isResource) { return [] }
			return cls.relationships::entries()
	             .filter(([,desc]) => desc.cardinality.max === 1)
	             .map(([key, desc]) => ({
	                 key,
	                 desc,
		             aliases: desc.shortcutKey ? [desc.shortcutKey] : []
	             }));
		}


		//////////////
		// instance //
		//////////////

		/**
		 * Create a new instance of this field on a specific entity.
		 * @protected
		 * @param {Object}    options
		 * @param {Entity}    options.owner                       - the entity on which to create this field
		 * @param {string}    options.key                         - the key on that entity corresponding to this field
		 * @param {Object}    options.desc                        - the descriptor for what kind of field this is
		 * @param {string}   [options.initialValue]               - the initial value for this field
		 * @param {Iterable} [options.aliases=[]]                 - other keys to which this field should answer
		 * @param {Object}   [options.aliasInitialValues=[]]      - other keys to which this field should answer
		 * @param {boolean}  [options.setValueThroughSignal=true] - whether signals sent to `field.p('value')` should be accepted
		 * @param {Object}   [options.valueTrackerOptions={}]     - the `ValueTracker` options to use for each field's observable properties
		 */
		constructor(options) {
			super(options);
			const { owner, key, desc, initialValue, aliasInitialValues } = options;

			/* set the initial value */
			const initialShortcutValue = aliasInitialValues[desc.shortcutKey];
			constraint(!initialValue || !initialShortcutValue, humanMsg`
				You cannot set the fields '${key}' and '${desc.shortcutKey}'
				at the same time for a ${this.constructor.singular}.
			`);
			this[$$initSet](
				[!initialValue        ::isUndefined(), () => initialValue        ::callOrReturn(owner)],
				[!initialShortcutValue::isUndefined(), () => initialShortcutValue::callOrReturn(owner)],
				[desc.cardinality.min === 0,           () => null]
			);

			owner.p('fieldsInitialized').filter(v=>!!v).take(1).subscribe(() => {

				/* synchronize with the other side */
				this.p('value').startWith(null).distinctUntilChanged().pairwise().subscribe(([prev, curr]) => {
					if (prev) { prev.fields[desc.codomain.keyInResource].delete(owner) }
					if (curr) { curr.fields[desc.codomain.keyInResource].add   (owner) }
				});

				/* pull in values set in sub-fields */
				for (let subCls of desc.relationshipClass.extendedBy) {
					const subFieldKey = subCls.keyInResource[desc.keyInRelationship];
					const subField = owner.fields[subFieldKey];
					if (!subField) { continue }
					subField.p('value').subscribe( this.p('value') );
				}

			});
		}

		/**
		 * @return {Set} A new `Set` data-structure populated with
		 *               the entity in this field, or empty.
		 */
		getAll() {
			let val = this.get();
			return new Set(val === null ? [] : [val]);
		}

		/**
		 * Set this field to a new entity, removing the previous one.
		 * @param {Entity}   newValue - the new entity to be put in this field
		 * @param {Object}  [options={}]
		 * @param {boolean} [options.ignoreReadonly=false]   - allow this field's value to be changed even if it is read-only
		 * @param {boolean} [options.ignoreValidation=false] - don't validate the new cardinality or value
		 */
		add(newValue, options) {
			this.set(newValue, options);
		}

		/**
		 * Set this field to `null` if the given entity corresponds to the entity currently in this field.
		 * @param {Entity}   oldValue - the entity to be removed from this field
		 * @param {Object}  [options={}]
		 * @param {boolean} [options.ignoreReadonly=false]   - allow this field's value to be changed even if it is read-only
		 * @param {boolean} [options.ignoreValidation=false] - don't validate the new cardinality
		 */
		delete(oldValue, options) {
			if (this.get() === oldValue) {
				this.set(null, options);
			}
		}

		/**
		 * How to properly convert a value in this field to JSON (plain data).
		 * @param {*}       value                 - the value to convert to JSON
		 * @param {Object} [options={}]
		 * @param {Class}  [options.requireClass] - the only `Entity` subclass accepted for the output, optionally
		 */
		static valueToJSON(value, options = {}) {
			let requireClass;
			({requireClass, ...options} = options);
			if (!value) { return value }
			if (requireClass && requireClass !== value.class) { return undefined }
			return env.Entity.normalizeAddress(value, options);
		}

		/**
		 * How to properly convert a JSON value to a value appropriate for this type of field.
		 * @param {*}         json               - the JSON value to convert
		 * @param {Object}   [options={}]
		 * @param {Function} [options.getEntity] - a function that returns an entity corresponding to a given address
		 */
		static jsonToValue(json, options = {}) {
			if (json === null) { return null }
			const {getEntity} = options;
			if (json instanceof env.Entity) {
				return json;
			} else if (getEntity::isFunction()) {
				return getEntity(json);
			}
		}

		/**
		 * Validate a certain value as appropriate for this field.
		 * Throws an exception if the value is invalid. Otherwise does nothing.
		 * @param {*}      val        - the value to test
		 * @param {Array} [stages=[]] - the validation stages to validate for (e.g., 'commit')
		 */
		validate(val, stages = []) {

			const notGiven = val::isNull() || val::isUndefined();

			if (stages.includes('commit')) {
				/* if there's a minimum cardinality, a value must have been given */
				constraint(!notGiven || this[$$desc].cardinality.min === 0, humanMsg`
					No value given for required field
					${this[$$owner].constructor.name}#${this[$$key]}.
				`);
			}

			/* the value must be of the proper domain */
			const expectedResourceClass = this[$$desc].codomain.resourceClass;
			const hasCompatibleType = expectedResourceClass.hasInstance(val);
			constraint(notGiven || hasCompatibleType, humanMsg`
				Invalid value '${val}' given for field ${this[$$owner].constructor.name}#${this[$$key]}.
			`);

		}

		/**
		 * Validate a certain value as appropriate for this field.
		 * Throws an exception if the value is invalid. Otherwise does nothing.
		 * @param {Entity}  element    - the value to test
		 * @param {Array}  [stages=[]] - the validation stages to validate for (e.g., 'commit')
		 */
		validateElement(element, stages = []) {
			return this.validate(element, stages);
		}

	}

	return env.registerFieldClass('Rel1Field', Rel1Field);
};

