		1: [CanonicalTree,       '0..*', { key: 'childBranches' }],
		2: [CanonicalTreeBranch, '1..1', { key: 'parentTree'    }],

	});

	const BranchesTo = M.RELATIONSHIP({

		name: 'BranchesTo',

		extends: PullsIntoTypeDefinition,

		singular: "branches to",
