        let group = Group.new();

        group.elements.add(lyph1);
        group.elements.add(lyph2);

        expect([...group.elements]).to.eql([lyph1, lyph2]);
    });

    it("trying to instantiate abstract class Has", () => {
        const {Lyph, HasPart} = environment.classes;

        let subLyph = Lyph.new({ name: 'Sublyph' });

        let layer1 = Lyph.new({ name: 'Vessel Wall' });

        let layer2 = Lyph.new({
            name: 'Blood Layer',
            parts: [ subLyph ]
        });

        let bloodVessel = Lyph.new({
            name: 'Blood Vessel',
            layers: [ layer1, layer2 ]
        });

        expect(() => { layer1.parts.add(subLyph) }).not.to.throw();

        expect([...layer1.parts])   .to.include(subLyph);
        expect([...layer1.children]).to.include(subLyph);
        expect([...subLyph.parents]).to.include(layer1);
        expect([...subLyph.parents]).to.include(layer2);
        expect(subLyph['<--HasPart'].size).to.equal(2);
        expect([...subLyph['<--HasPart']][0]).to.be.instanceOf(Lyph);
        expect([...subLyph['<--Has']][0])    .to.be.instanceOf(Lyph);

    });

    it("relationship mismatch", () => {
        const {Lyph, Type} = environment.classes;

        let blood = Lyph.new({ name: "Blood" });
        let bloodType = Type.new({ name: blood.name, definition: blood });
        blood.types.add(bloodType);

        expect(blood.types.size).to.equal(1);
        expect([...blood.types][0]).to.equal(bloodType);
    });

    it("export manually defined plural", () => {
        const {Process, Causality} = environment.classes;

        let process   = Process.new({ name: "Blood advection" });
        let causality = Causality.new({});

        expect(process.constructor).to.have.a.property('plural', "processes");
        expect(causality.constructor).to.have.a.property('plural', "causalities");
    });

    it("auto-synchronized border-natures?", async () => {

        const {Lyph, Border} = environment.classes;

        let lyph = Lyph.new({
            longitudinalBorders: [
                Border.new(),
                Border.new()
            ]
        });

        await new Promise((resolve) => { setTimeout(resolve, 10) });

        expect(new Set([
            [...lyph.longitudinalBorders][0].nature,
            [...lyph.longitudinalBorders][1].nature
        ]).size).to.equal(2);

        // To compare, this was the nature of the original bug.
        // The default value of properties was shared among entities:
        let singleArray = [];
        expect(new Set([
            singleArray,
            singleArray
        ]).size).to.equal(1);

    });

});

import {describe, it, expect, beforeEach} from '../test.helper';

import ObservableSet, {setEquals} from '../../src/util/ObservableSet';

describe("ObservableSet", () => {

	let s;
	beforeEach(() => {
		s = new ObservableSet();
	});

	it("can replay the current set on a new 'add' subscription", () => {

		let added   = new Set();
		let deleted = new Set();

		s.e('add')   .subscribe((v) => { added  .add(v) });
		s.e('delete').subscribe((v) => { deleted.add(v) });

		s.add(1);
		s.add(2);
		s.delete(1);

		expect([...added])    .to.include(1);
		expect([...added])    .to.include(2);
		expect([...added]).not.to.include(3);
		expect([...deleted])  .to.include(1);

	});

	it("can replay the current set on a new 'add' subscription", () => {

		let added = new Set();

		s.add(1);
		s.add(2);
		s.add(3);
		s.delete(1);

		s.e('add').subscribe((v) => { added.add(v) });

		s.add(4);
		s.add(5);
		s.delete(2);

		expect([...added]).to.include(2);
		expect([...added]).to.include(3);
		expect([...added]).to.include(4);
		expect([...added]).to.include(5);
		expect([...added]).to.not.include(1);

	});

	it("can use replay to synchronize the entire set through the 'add' and 'delete' subjects", () => {

		let syncedSet = new Set();

		s.add(1);
		s.add(2);
		s.add(3);
		s.delete(1);

		s.e('add'   ).subscribe(::syncedSet.add);
		s.e('delete').subscribe(::syncedSet.delete);

		s.add(4);
		s.add(5);
		s.delete(2);

		expect([...syncedSet]).to.include(3);
		expect([...syncedSet]).to.include(4);
		expect([...syncedSet]).to.include(5);
		expect([...syncedSet]).to.not.include(1);
		expect([...syncedSet]).to.not.include(2);

		expect([...s]).to.eql([...syncedSet]);

	});

	it("can emit a copy of the current set every time something is added or deleted", () => {

		let values = [];

		s.p('value').subscribe((v) => { values.push(v) });

		s.add(1);
		s.add(2);
		s.delete(1);

		expect([...values[0]]).to.eql([    ]);
		expect([...values[1]]).to.eql([1   ]);
		expect([...values[2]]).to.eql([1, 2]);
		expect([...values[3]]).to.eql([   2]);

	});

});
